using Core.AasLookupService.Templates;
using Core.Dtos;
using Core.Dtos.AppSettingsOptions;
using Core.Errors;
using Core.RepoProxyClient.Interfaces;
using Core.Shared;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Options;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;
using Newtonsoft.Json.Serialization;

namespace Core.AasLookupService;

/// <summary>
/// Uses an aas in the repository to store the assetIds and the corresponding aasIds.
/// There is one aas which represents the registry.
/// For each assetId there is a submodel with the idShort and id "assetId".
/// The aasIds are stored blank separated in the value. 
/// </summary>
public class AasBasedAasIdStorage : IAasIdStorage
{
    private readonly ILogger<AasBasedAasIdStorage> _logger;
    private readonly IRepoProxyClient _repoProxyClient;
    private readonly RepoProxyOptions _repoProxyOptions;

    public AasBasedAasIdStorage(
        ILogger<AasBasedAasIdStorage> logger,
        IRepoProxyClient repoProxyClient,
        IOptions<RepoProxyOptions> repoProxyOptions)
    {
        _repoProxyOptions = repoProxyOptions.Value ?? throw new ArgumentNullException(nameof(repoProxyOptions));
        _logger = logger;
        _repoProxyClient = repoProxyClient;
    }

    /// <inheritdoc />
    public async Task AddAasIdToAssetAsync(string base64EncodedAssetId, string base64EncodedAasId)
    {
        try
        {
            _logger.LogDebug("AddAasAsync - base64EncodedAssetId: {AssetId}, base64EncodedAasId: {AasId}", base64EncodedAssetId, base64EncodedAasId);
            var base64EncodedAasIdsInStorage = await GetAasIdsForAssetIdAsync(base64EncodedAssetId);
            if (base64EncodedAasIdsInStorage.Contains(base64EncodedAasId))
            {
                _logger.LogDebug("base64EncodedAasId {AasId} already assigned to base64EncodedAssetId {AssetId} - return", base64EncodedAasId, base64EncodedAssetId);
                return;
            }

            base64EncodedAasIdsInStorage.Add(base64EncodedAasId);
            await PutNewAasIdsSubmodel(base64EncodedAssetId, base64EncodedAasIdsInStorage);
        }
        catch (RepoProxyException repoProxyException)
        {
            _logger.LogDebug("Submodel for base64EncodedAssetId {AssetId} does not exist. Create new submodel. Error: {Message}", base64EncodedAssetId, repoProxyException.Message);
            await PostNewAasIdsSubmodel(base64EncodedAssetId, new List<string> { base64EncodedAasId }).ConfigureAwait(true);
        }
    }

    /// <inheritdoc />
    public async Task RemoveAasIdFromAssetAsync(string base64EncodedAssetId, string base64EncodedAasId)
    {
        _logger.LogDebug("DeleteAasAsync - base64EncodedAssetId: {AssetId}, base64EncodedAasId: {AasId}", base64EncodedAssetId, base64EncodedAasId);
        var base64EncodedAasIdsInStorage = await GetAasIdsForAssetIdAsync(base64EncodedAssetId).ConfigureAwait(true);
        base64EncodedAasIdsInStorage.Remove(base64EncodedAasId);

        if (base64EncodedAasIdsInStorage.Count == 0)
        {
            // TODO: delete submodel for this assetId if there is no more aasId.
        }

        await PutNewAasIdsSubmodel(base64EncodedAssetId, base64EncodedAasIdsInStorage);
    }

    /// <inheritdoc />
    public async Task<List<string>> GetAasIdsForAssetIdAsync(string base64EncodedAssetId)
    {
        var encodedAssetSubmodelId = Base64StringDeAndEncoder.EncodeTo64(base64EncodedAssetId);
        var pathToCall = $"{_repoProxyOptions.SubmodelPath}/{encodedAssetSubmodelId}/submodel-elements/AasIds/$value";
        var (isSuccess, result) = await _repoProxyClient.GetAsync(pathToCall).ConfigureAwait(true);

        if (!isSuccess)
        {
            return new List<string>();
        }

        var response = JArray.Parse(result);
        var aasIds = response.First?.SelectToken("AasIds");

        if (string.IsNullOrWhiteSpace(aasIds?.ToString()))
        {
            return new List<string>();
        }

        var blankSeparatedAasIds = aasIds.Value<string>();
        return blankSeparatedAasIds != null
            ? new List<string>(blankSeparatedAasIds.Split(' '))
            : new List<string>();
    }

    /// <summary>
    /// Creates a new aasIds submodel for the given parameters and replaces the old submodel.
    /// </summary>
    /// <param name="base64EncodedAssetId">base64 encoded assetId</param>
    /// <param name="base64EncodedAasIds">List of base64 encoded aasIds</param>
    private async Task PutNewAasIdsSubmodel(string base64EncodedAssetId, IEnumerable<string> base64EncodedAasIds)
    {
        var aasIdsSubmodel = TemplateProvider.GetAasIdsSubmodel(base64EncodedAssetId, string.Join(" ", base64EncodedAasIds));
        var pathToCall = $"repo/submodels/{Base64StringDeAndEncoder.EncodeTo64(base64EncodedAssetId)}";
        await _repoProxyClient.PutAsync(pathToCall, aasIdsSubmodel).ConfigureAwait(true);
    }

    /// <summary>
    /// Creates a new aasIds submodel for the given parameters, posts it to the repository and
    /// creates a submodel reference to the registry aas.
    /// </summary>
    /// <param name="base64EncodedAssetId">base64 encoded assetId</param>
    /// <param name="base64EncodedAasIds">List of base64 encoded aasIds</param>
    private async Task PostNewAasIdsSubmodel(string base64EncodedAssetId, IEnumerable<string> base64EncodedAasIds)
    {
        var aasIdsSubmodel = TemplateProvider.GetAasIdsSubmodel(base64EncodedAssetId, string.Join(" ", base64EncodedAasIds));
        await _repoProxyClient.PostAsync(_repoProxyOptions.SubmodelPath, aasIdsSubmodel).ConfigureAwait(true);

        var submodelReference = new SubmodelReference(
            new List<Key> { new("Submodel", base64EncodedAssetId) }, "ModelReference");
        var submodelReferenceJson = JsonConvert.SerializeObject(submodelReference, new JsonSerializerSettings
        {
            ContractResolver = new CamelCasePropertyNamesContractResolver()
        });

        await _repoProxyClient.PostAsync(_repoProxyOptions.RegistryAasPath, submodelReferenceJson).ConfigureAwait(true);
    }
}```
